ruleToc.chapterList:
+<js>
org.jsoup.Jsoup.parse(result).select("#list a").toArray().map(ch=>{
  var name=ch.text();
  var data=String(ch.attr("href")).match(/\/([a-zA-Z]+)\/(\d+)\/Chapter_(\d+)\./);
  var fid=data[1]=="NvRen"?2:1;
  var bid=data[2];
  var cid=data[3];
  var header={
"headers":'{Referer:"http://www.dfu7.com/NanRen/1/Chapter_1.Html"}'
};
  var url='http://www.dfu7.com/JS/Chapter.Asp?Z='+cid+'&S='+bid+'&C='+fid+','+JSON.stringify(header);

  return {title:name,link:url};
})
</js>

