if(mch!=null){
  let bid=doc.body().attr("data-bid");
  let p=Math.ceil(String(mch.text()).match(/\d+/)[0]/900);
  let mlist=[];
  for(let i=1;i<p+1;i++){
    let url="https://www.shuchong.info/api/,"+JSON.stringify({
  "method": "POST",
  "body": "action=list&bid="+bid+"&page="+i
});
    Array.prototype.push.apply(mlist,
JSON.parse(java.ajax(url)).data.map(ch=>"<li><a href=\"/chapter/"+bid+"/"+ch.id+".html\">"+ch.cN+"</a>"+ch.uT+"</li>")
    );
  }
  mch.html(mlist.join("\n"));
}

doc.select(".chapter-list li").toArray().map(ch=>{
var c=ch.selectFirst("a");
return {title:c.text(),info:ch.ownText(),link:c.attr("href")};
})
</js>

+<js>
var bid = parseInt(result.match(/data-bid="(\d+)/)[1]);
var reg = 'href="/chapter/[^/]+/([^"]+)[^>]+>([^<]+)[^>]+>([^<]+)';
var cList = result.match(new RegExp(reg, 'g')).map((item) => {
	var ret = item.match(reg);
	return { cN: ret[2], uT: ret[3].trim(), id: parseInt(ret[1]) + bid };
});
var hider = result.match(/查看隐藏章节[^<]+/);
if (hider) {
	let p = Math.ceil(hider[0].match(/\d+/)[0] / 900);
	for (let i = 1; i <= p; ++i) {
		let url = 'https://www.zhaishuyuan.com/api/,' + JSON.stringify({ charset: 'gbk', method: 'POST', body: 'action=list&bid=' + bid + '&page=' + i });
		Array.prototype.push.apply(cList, JSON.parse(java.ajax(url)).data);
	}
}
cList.sort((a, b) => (a.id < b.id ? -1 : 1)).map((item) => {
	item.id = '/chapter/' + bid + '/' + (item.id - bid);
	return item;
});
</js>

