var base="https://app2.motie.com/books/{}/detail";
var list=[];
if(baseUrl.indexOf("channels")>-1){
var clist=[];
JSON.parse(result).items.forEach(book=>{
  book.dataSourceList.forEach(bk=>{
    if(bk.dataList)
     Array.prototype.push.apply(clist,
     eval(bk.dataList));
  });
});
clist.forEach(bk=>{
if(bk.endTime)
  if(new Date().getTime() <= new Packages.java.text.SimpleDateFormat("yyyy-MM-dd HH:mm:ss").parse(bk.endTime).getTime())
list.push({
a:bk.bookName,
b:bk.penName,
c:bk.category,
d:bk.introduction,
e:"限免结束:"+bk.endTime,
f:bk.imgUrl,
g:base.replace("{}",bk.bookId)
});
});
}else{
list=JSON.parse(result).data.items.map(bk=>{
var tFormat=t=>t<10?"0"+t:t;
var clock = new Date(bk.lastChapterTime);

var time = clock.getFullYear()+"/" + tFormat(clock.getMonth() + 1)+"/" + tFormat(clock.getDate()) + " " + tFormat(clock.getHours()) + ":" + tFormat(clock.getMinutes()) + ":" + tFormat(clock.getSeconds());

return {a:bk.bookName,
b:bk.authorName,
c:bk.categoryVO.name+","+bk.words,
d:bk.introduction,
e:time,
f:bk.bookIconUrl,
g:base.replace("{}",bk.bookId)
};
});
}
list</js>

