var doc=org.jsoup.Jsoup.parse(result);
var name=doc.getElementById('novelName').text();
doc.select(".DivTable a").toArray().map(a=>{
  var href=a.attr("href");
  var link=href.startsWith('http')?href:('https:'+href);
  var isVip=link.indexOf("vip")>-1;
  var title=a.attr('title').replace(name+':','').replace(name+' ','');
  var infos=title.split('\n');
  return {text:infos[0],vip:isVip,url:link,info:infos[1]};
})
</js>

