ruleSearch.bookList:
id.waterfall@.item
@js:
result.toArray().filter(div=>String(div.select('.chapter a').text()).trim()).slice(0, 5).map(o=> {
    var img = String(o.select('.pic img').attr('data-original'));
    return {
        name: o.select('.title a').text(),
        author: String(o.select('.nickname').text()).replace(/\s*\/.+/, '').trim(),
        cover: img && !img.includes('nocover') ? img: 'http://suo.im/6mlA9F',
        tags: String(o.select('.num a').toArray().map(o =>o.attr('title'))),
        latest: String(o.select('.chapter').text()).replace(/\s*\[(.+)\]/, '（$1）'),
        intro: String(o.select('.intro').text()).replace(/[\s\t]+/g, '').trim(),
        catalogUrl: o.select('.title a').attr('href')
    }
});

ruleToc.chapterList:
@js:
var J = org.jsoup.Jsoup.parse(result);
var ycnum = J.select('.ycnum').text();
var id = baseUrl.match(/(id=|\/)(\d+)/)[2];
var tocs;
if (ycnum) {
    ycnum = parseInt(String(ycnum)) + 30;
    tocs = org.jsoup.Jsoup.connect('https://www.xiashu.cc/api/ajax/zj?id=' + id + '&num=' + ycnum+'&order=asc').get().select('a[href]')
} else {
    tocs = J.select('#detaillist a[href]')
}
tocs.toArray().map(a =>({
    text: a.text(),
    href: a.attr('href')
})).filter(a =>a.href);

