ruleSearch.bookList:
@css:.sort-con-wp > a
<js>
//只保留书名长度最短的图书。
var len = (o) => String(o.select('h3').text()).length;
var list = result.toArray();
var min = Math.min.apply(null, list.map(e => len(e)));
result = list.filter(e => len(e) === min).map(v => java.ajax('https://www.yooread.net'+v.attr('href')));
</js>

ruleToc.chapterList:
+<js>var ih = java.get('InfoHTML'); result = ih !== java.get('') ? ih : result;</js>
@css:#booklist table:not(strong:contains(最新章节)) li a
<js>
var getCid = (link) => link.href.match(/(\d+)\.html$/)[1];
result = result.toArray().map(e => ({
  text: e.text(), href: String(e.attr('href'))
})).filter(v => v.href.length > 0).sort((a,b) => getCid(a) - getCid(b));
</js>
